var app=angular.module("hermes_app",["ui.mask"]);app.controller("sms_ctrl",function(o,r){o.url="/api/sms",o.clearForm=function(){o.form.sms={descricao_destinatario:"",numero_destinatario:"",texto:""}},o.handleErrorResponse=function(o){switch(o.status){case 200:console.log("OK");break;case 422:for(var r in o.data)toastr.error(o.data[r])}},o.formSubmit=function(){r({method:"post",url:o.url,data:o.form.sms}).then(function(r){toastr.success("O Sms foi enviado com sucesso!"),o.clearForm()},function(o){console.log(o),toastr.error("Fail")})}});